@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Mono:wght@400;500&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  /* Custom semantic tokens */
  --color-surface: var(--surface);
  --color-surface-elevated: var(--surface-elevated);
  --color-hero-bg: var(--hero-bg);
  --color-hero-text: var(--hero-text);
  --color-stat-bg: var(--stat-bg);

  --font-sans: 'IBM Plex Sans', sans-serif;
  --font-mono: 'IBM Plex Mono', monospace;
}

:root {
  --radius: 0.5rem;

  /* Off-white warm base */
  --background: hsl(36 20% 97%);
  --foreground: hsl(230 25% 12%);

  --card: hsl(0 0% 100%);
  --card-foreground: hsl(230 25% 12%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(230 25% 12%);

  /* Deep indigo primary */
  --primary: hsl(237 55% 36%);
  --primary-foreground: hsl(0 0% 100%);
  --primary-glow: hsl(237 55% 55%);

  --secondary: hsl(237 20% 94%);
  --secondary-foreground: hsl(237 55% 36%);

  --muted: hsl(237 15% 94%);
  --muted-foreground: hsl(237 10% 48%);

  --accent: hsl(28 90% 55%);
  --accent-foreground: hsl(0 0% 100%);

  --destructive: hsl(0 72% 51%);
  --border: hsl(237 15% 88%);
  --input: hsl(237 15% 88%);
  --ring: hsl(237 55% 36% / 0.4);

  /* Chart colors */
  --chart-1: hsl(237 55% 46%);
  --chart-2: hsl(167 58% 42%);
  --chart-3: hsl(28 90% 55%);
  --chart-4: hsl(320 55% 52%);
  --chart-5: hsl(48 95% 52%);

  /* Sidebar */
  --sidebar: hsl(237 40% 14%);
  --sidebar-foreground: hsl(237 20% 88%);
  --sidebar-primary: hsl(237 55% 60%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(237 30% 22%);
  --sidebar-accent-foreground: hsl(237 20% 92%);
  --sidebar-border: hsl(237 30% 20%);
  --sidebar-ring: hsl(237 55% 50%);

  /* Custom surfaces */
  --surface: hsl(36 20% 97%);
  --surface-elevated: hsl(0 0% 100%);
  --hero-bg: hsl(237 40% 14%);
  --hero-text: hsl(237 20% 92%);
  --stat-bg: hsl(237 20% 94%);

  /* Gradients */
  --gradient-primary: linear-gradient(135deg, hsl(237 55% 36%), hsl(237 55% 55%));
  --gradient-hero: linear-gradient(135deg, hsl(237 40% 14%), hsl(237 40% 22%));
  --gradient-subtle: linear-gradient(180deg, hsl(36 20% 97%), hsl(237 15% 94%));
  --gradient-card: linear-gradient(135deg, hsl(0 0% 100%), hsl(237 20% 97%));

  /* Shadows */
  --shadow-sm: 0 1px 3px hsl(237 25% 20% / 0.08);
  --shadow-md: 0 4px 12px hsl(237 25% 20% / 0.10);
  --shadow-lg: 0 10px 30px -8px hsl(237 55% 36% / 0.18);
  --shadow-elegant: 0 8px 24px -4px hsl(237 55% 36% / 0.22);
  --shadow-glow: 0 0 32px hsl(237 55% 55% / 0.25);

  /* Transitions */
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
  --transition-bounce: all 0.3s cubic-bezier(0.34, 1.56, 0.64, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 15px;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
  }
}

/* Utility classes using design tokens */
@layer utilities {
  .font-display {
    font-family: 'IBM Plex Sans', sans-serif;
    font-weight: 700;
    letter-spacing: -0.02em;
  }

  .hero-section {
    background: var(--gradient-hero);
    color: hsl(var(--hero-text));
  }

  .card-elevated {
    background: var(--surface-elevated);
    box-shadow: var(--shadow-md);
    border: 1px solid hsl(237 15% 88%);
  }

  .card-stat {
    background: hsl(237 20% 94%);
    border: 1px solid hsl(237 20% 88%);
  }

  .nav-tab {
    font-weight: 500;
    letter-spacing: 0.01em;
    transition: var(--transition-smooth);
    border-bottom: 2px solid transparent;
  }

  .nav-tab.active {
    border-bottom-color: hsl(237 55% 36%);
    color: hsl(237 55% 36%);
  }

  .badge-status-geplant {
    background: hsl(48 95% 88%);
    color: hsl(48 80% 30%);
    border: 1px solid hsl(48 80% 75%);
  }
  .badge-status-aktiv {
    background: hsl(167 58% 88%);
    color: hsl(167 58% 22%);
    border: 1px solid hsl(167 58% 68%);
  }
  .badge-status-abgeschlossen {
    background: hsl(237 20% 88%);
    color: hsl(237 30% 35%);
    border: 1px solid hsl(237 20% 75%);
  }
  .badge-status-abgesagt {
    background: hsl(0 72% 92%);
    color: hsl(0 72% 38%);
    border: 1px solid hsl(0 72% 78%);
  }

  .btn-primary {
    background: var(--gradient-primary);
    color: hsl(0 0% 100%);
    box-shadow: var(--shadow-elegant);
    transition: var(--transition-smooth);
    font-weight: 600;
  }
  .btn-primary:hover {
    box-shadow: var(--shadow-glow);
    transform: translateY(-1px);
  }

  .table-row-hover:hover {
    background: hsl(237 20% 97%);
  }

  .input-focus:focus {
    border-color: hsl(237 55% 50%);
    box-shadow: 0 0 0 3px hsl(237 55% 50% / 0.15);
  }
}

