        <Button onClick={openNew} style={{ background: 'var(--gradient-primary)', color: 'hsl(0 0% 100%)', boxShadow: 'var(--shadow-elegant)' }} className="gap-2">
          <Plus size={16} /> Kurs hinzufügen
